## DROP TABLE IF EXISTS `formmail_id`;CREATE TABLE formmail_id (  id_form smallint(5) NOT NULL auto_increment,  desc_form varchar(60) NOT NULL default '',  admin varchar(5) default NULL,  groupe varchar(255) default NULL,  email varchar(255) default NULL,  expe varchar(5) default NULL,  text_index text NOT NULL,  text_form text NOT NULL,  form_order smallint(2) NOT NULL default '0',  form_req varchar(5) NOT NULL default '0',  PRIMARY KEY  (`id_form`),  UNIQUE KEY `` (`desc_form`)) ENGINE=MyISAM;# DROP TABLE IF EXISTS `formmail`;CREATE TABLE formmail (  id_form int(5) NOT NULL default '0',  ele_id smallint(5) unsigned NOT NULL auto_increment,  ele_ENGINE varchar(10) NOT NULL default '',  ele_caption varchar(255) NOT NULL default '',  ele_order smallint(2) NOT NULL default '0',  ele_req tinyint(1) NOT NULL default '1',  ele_value text NOT NULL,  ele_display tinyint(1) NOT NULL default '1',  PRIMARY KEY  (`ele_id`),  KEY `ele_display` (`ele_display`),  KEY `ele_order` (`ele_order`)) ENGINE=MyISAM;# DROP TABLE IF EXISTS `formmail_menu`;CREATE TABLE formmail_menu (  menuid int(4) unsigned NOT NULL auto_increment,  position int(4) unsigned NOT NULL,  indent int(2) unsigned NOT NULL default '0',  itemname varchar(60) NOT NULL default '',  margintop varchar(12) NOT NULL default '0px',  marginbottom varchar(12) NOT NULL default '0px',  itemurl varchar(100) NOT NULL default '',  bold tinyint(1) NOT NULL default '0',  mainmenu tinyint(1) NOT NULL default '0',  membersonly tinyint(1) NOT NULL default '1',  status tinyint(1) NOT NULL default '1',  PRIMARY KEY  (menuid),  KEY idxmymenustatus (status)) ENGINE=MyISAM;